import React,{Component,Fragment} from 'react'

export default class Person extends Component{
    a=()=>{
        let id = new Date.now()
        const {name:{value:v},age:{value:a}} = this
        this.props.add({id:id+1,name:v,age:a*1})
    }
    render(){
        
        return (
            <Fragment>
                <h1>{`我是Person组件,Count组件的数字为:${this.props.count}`}</h1>
                <input ref={c=>{this.name=c}} type="text" placeholder="输入姓名"/>
                <input ref={c=>{this.age=c}}type="text" placeholder="输入年龄为"/>
                <button onClick={this.a}>提交</button>
                <ul>
                   {this.props.person.map((p)=>{
                       return <li key={p.id}>{`姓名：${p.name}----年龄:${p.age}`}</li>
                   })}
                </ul>
            </Fragment>
        )
    }
}
