import React,{Component} from 'react'
import uuid from 'react-uuid' 
import Header from './Header'
import Footer from './Footer'
import List from './List'
import './App.css'


export default class App extends Component{



  state = {todos:[
    {id:uuid(),todo:"吃饭",done:false},
    {id:uuid(),todo:"睡觉",done:false},
    {id:uuid(),todo:"敲代码",done:false}
  ]}
  doneCount = ()=>{
      let count=0
      let {todos} = this.state
      todos.map((n)=>{
          if (n.done === true) {
               count++
          }
          return true
      })
      return count
  }
  todoCount = ()=>{
    return this.state.todos.length
  }

  changeDone= (id,done)=>{
    let newtodos = this.state.todos.map((n)=>{
          if(n.id === id){
            return {...n,done}
          }else{
            return {...n}
          }
   })

   this.setState({todos:newtodos})
}
  addtodo = (todo)=>{
     let newtodo = {id:uuid(),todo,done:false}
     let newtodos = this.state.todos
     this.setState({todos:[newtodo,...newtodos]})
  }
  deleteTodo=(id)=>{
    const {todos} = this.state
    let newtodos =todos.filter((n)=>{
      return n.id !== id
    })
    this.setState({todos:newtodos})
 }

dqh = ()=>{
  let newtodos = this.state.todos.map((n)=>{
           n.done = !n.done
           return n
    })
    this.setState({todos:newtodos})
}

