  render(){
    return (
       <div className="whole">
         <Header addtodo={this.addtodo}/>
         <List todos={this.state.todos} changeDone={this.changeDone} deleteTodo={this.deleteTodo}/>
         <Footer todoCount={this.todoCount} doneCount={this.doneCount} dqh={this.dqh}/>
       </div>
    )
  }
}
