import React,{Component} from 'react'
import './index.css'
export default class Item extends Component{
     state={
         flag:false
     }
    render(){
        const {id,todo,done} = this.props
        const {deleteTodo} = this.props

        return (
            <li onMouseEnter={()=>{this.setState({flag:true})}}  onMouseLeave={()=>{this.setState({flag:false})}}>
                <input  type="checkbox" checked={done} onChange={(even)=>{this.props.changeDone(id,!done)}} /><span>{todo}</span>
                <button style={{display:this.state.flag?'block':'none'}} onClick={()=>{deleteTodo(id)}}>删除</button>
            </li>
        )
    }
}
