import {INCREASEMENT,DECREASEMENT} from './constant'
const initState = 0
export default function countReducer(preState=initState,action) {
     const{type,data} = action
     console.log(preState);
     switch (type) {
         case 'increase':
             return preState+data
         case 'decrease':
             return preState-data
         default:
             return preState
     }

}
