import React,{Component} from 'react'
import axios from 'axios'
import './index.css'
export default class Header extends Component {
    
    state

    serch = ()=>{
        const{serchItem:{value}} = this
        axios.get(`https://github.com/search?q=${value}`).then(
            response=>{
               console.log(response);
            },
            error=>{
              console.log(error.message);
            }
        )
    }
    render(){
        return (
            <div className="header">
                <h1>搜索github用户</h1>
                <input placeholder="输入用户名" ref={(c)=>{this.serchItem = c}} />
                <button onClick={this.serch}>搜索</button>
            </div>
        )
    } 

}
