import React,{Component} from 'react'
import './index.css'
export default class Footer extends Component{


    
    render(){
        let {doneCount,todoCount} = this.props
        return (
            <div className="footer">
                <input type="checkbox" checked={doneCount() === todoCount()&& todoCount()!==0} onChange={this.props.dqh}/>
                <span>已完成{doneCount()} / 全部{todoCount()}</span>
                <button onClick={this.props.deleteAll}>清除已完成任务</button>
            </div>
        )
    }
}
