import React,{Component} from 'react'
import axios from 'axios'
import Pubsub from 'pubsub-js'
import './index.css'
export default class Header extends Component {
   
   
    updateState = (stateobj)=>{
        this.setState(stateobj)
    }
    serch = ()=>{
        const{serchItem:{value}} = this
        Pubsub.public('戴清海',{isFist:false,isloading:true})
        axios.get(`http://api.github.com/search/users?q=${value}`).then(
            response=>{
               Pubsub.public('戴清海',{users:response.data.items,isloading:false})
            },
            error=>{
                Pubsub.public('戴清海',{error:error.message,isloading:false})
            }
        )
    }
    render(){
        return (
            <div className="header">
                <h1>搜索github用户</h1>
                <input placeholder="输入用户名" ref={(c)=>{this.serchItem = c}} />
                <button onClick={this.serch}>搜索</button>
            </div>
        )
    } 

}
