import React,{Component} from 'react'
import './index.css'
export default class Item extends Component{
    render(){
        const {id,todo,done} = this.props
        const {deleteTodo} = this.props
        
        return (
            <li>
                <input type="checkbox" checked={done} onChange={(even)=>{this.props.changeDone(id,!done)}} /><span>{todo}</span>
                <button onClick={deleteTodo(id)}>删除</button>
            </li>
        )
    }
}
