import React,{Component} from 'react'
import store from '../redux/store'
// import {createAddAction,createDecreaseAction,creastanyscAction} from '../redux/count_action'
export default class Count extends Component{
    
    // componentDidMount(){
    //     store.subscribe(()=>{
    //         this.setState({})
    //     })
    // }

    add =()=>{
        const {value} = this.select
        this.props.add(value*1)
    }
    decrease = ()=>{
        const {value} = this.select
        this.props.jian(value*1)
    }
    oddAdd=()=>{
        const {value} = this.select
        const count = store.getState()
        console.log(count);
        if (count %2 !== 0) {
            this.props.add(value*1)
        }
    }
    anycAdd = ()=>{
        const {value} = this.select
        this.props.anysc(value*1,1000)
    }

    render(){
        return (
            <div>
                <h1>当前求和为:{store.getState()}</h1>
                <select ref={c=>{this.select=c}}>
                    <option value="1">1</option>
                    <option value="2">2</option>
                    <option value="3">3</option>
                </select>
                <button onClick={this.add}>+</button>
                <button onClick={this.decrease}>-</button>
                <button onClick={this.oddAdd}>当前求和为奇数再加</button>
                <button onClick={this.anycAdd} >异步加</button>
            </div>
        )
    }
}
