import React,{Component} from 'react'
import Pubsub from 'pubsub-js'
import './index.css'
export default class List extends Component{
    state = {
        users:[],
        isFist:true,
        isloading:false,
        error:""
    }

    componentDidMount(){
        this.token = Pubsub.subscribe("戴清海",(n,data)=>{
            this.setState(data)
        })
    }
    componentWillUnmount(){
        Pubsub.unsubscribe(this.token)
    }
    render(){
        const {users,isFist,isloading,error} = this.state
        return (
            <div>
                {
                   isFirst? <h1>欢迎来到页面，请继续搜索</h1>:isloading?<h2>页面正在加载中</h2>:error?<h1>系统出错了</h1>
                   :users.map(user =>{
                    return (<div key={user.id} className="list"> 
                           <div>
                              <img src={user.avatar_url}  alt="header" />
                              <h2>{user.login}</h2>
                           </div>
                    </div>)
    
                   })
                }  
            </div>
        )
    }
}
